import React, {Component} from "react";
import {Messages} from "primereact/messages";
import {Card} from "primereact/card";
import {InputText} from "primereact/inputtext";
import {classNames} from "primereact/utils";
import {Button} from "primereact/button";

export class User extends Component {

    newUser = {
        name : "",
        middleName: "",
        lastName: "",
        email: "",
        phone: ""
    }

    constructor(props) {
        super(props);
        this.state = {
            submitted: false,
            user: this.newUser
        }

        this.saveUser = this.saveUser.bind(this);

    }

    onInputChange(e, name) {
        const val = (e.target && e.target.value) || '';
        let user = {...this.state.user};
        user[`${name}`] = val;

        this.setState({ user });
    }

    saveUser() {
        console.log(this.state.user);

    }


    render() {

        const productDialogFooter = (
            <React.Fragment>
                <div style={{ display: "flex" }}>
                    <Button label="Register" icon="pi pi-check" className="p-button-success mr-2"  style={{ marginLeft: "auto" }} onClick={this.saveUser} />
                </div>
            </React.Fragment>
        );

        return (
            <div style={{width:'80%', margin:'0 auto', marginTop:'40px'}}>
                <Messages ref={el => (this.messages = el)} />
                <Card title="Register" footer={productDialogFooter} >
                    <div className="col-12 md:col-4">
                        <label htmlFor="name">Name</label>
                        <div className="p-inputgroup">
                            <InputText id="name"
                                       value={this.state.name}
                                       onChange={(e) => this.onInputChange(e, "name")}
                                       required
                                       className={classNames({ 'p-invalid': this.state.submitted && !this.state.name })}/>
                        </div>
                        <label htmlFor="midName">Middle Name</label>
                        <div className="p-inputgroup">
                            <InputText id="midName"
                                       value={this.state.middleName}
                                       onChange={(e) => this.onInputChange(e, 'middleName')}
                                       required
                                       className={classNames({ 'p-invalid': this.state.submitted && !this.state.middleName })}/>
                        </div>
                        <label htmlFor="lastName">Last Name</label>
                        <div className="p-inputgroup">
                            <InputText id="lastName"
                                       value={this.state.lastName}
                                       onChange={(e) => this.onInputChange(e, "lastName")}
                                       required
                                       className={classNames({ 'p-invalid': this.state.submitted && !this.state.lastName })}/>
                        </div>
                        <label htmlFor="Email">Email</label>
                        <div className="p-inputgroup">
                            <InputText id="Email"
                                       value={this.state.email}
                                       onChange={(e) => this.onInputChange(e, "email")}
                                       required
                                       className={classNames({ 'p-invalid': this.state.submitted && !this.state.email })}/>
                        </div>
                        <label htmlFor="Phone">Phone</label>
                        <div className="p-inputgroup">
                            <InputText id="Phone"
                                       value={this.state.phone}
                                       onChange={(e) => this.onInputChange(e, "phone")}
                                       required
                                       className={classNames({ 'p-invalid': this.state.submitted && !this.state.phone })}/>
                        </div>
                    </div>
                </Card>
            </div>
        );
    }
}

export default User;
