import React, {Component} from "react";
import {classNames} from 'primereact/utils';
import {Card} from "primereact/card";
import {Button} from "primereact/button";
import {InputText} from "primereact/inputtext";
import { Messages } from 'primereact/messages';
import {RentService} from "../service/RentService";
import validator from 'validator'
import {DataTable} from "primereact/datatable";
import {Column} from "primereact/column";
import {Panel} from "primereact/panel";


export class Rents extends Component {

    emptyRent = {
        rental_id: 0,
        vin: '',
        client_id: '',
        totalAmount: 0.0,
        rentalDate: null,
        returnDate: null,
        statu: false
    };

    constructor(props) {
        super(props);

        this.state = {
            id_email:'',
            submitted: false,
            searchValid: false,
            rent: this.emptyRent,
            rents: {}
        }

        this.rentService = new RentService();

        this.searchRent = this.searchRent.bind(this);
        this.hideDialog = this.hideDialog.bind(this);
        this.showError = this.showError.bind(this);
        this.showErrorID = this.showErrorID.bind(this);
    }

    onInputChange(e) {
        this.state['id_email'] = ((e.target && e.target.value) || '').trim();
    }

    searchRent() {
        this.state.submitted = true;
        this.state.rent = '';
        if (this.state.id_email) {
            if (validator.isEmail(this.state.id_email)){
                console.log("Valid Email");
                //this.rentService.getRentEmail(this.state.id_email).then(data => {this.setState({rents: data})});
            } else if (validator.isInt(this.state.id_email)) {
                console.log("Valid ID");
                this.rentService.getRentId(this.state.id_email).then(data => {this.setState({rent: data})});
            } else {
                this.showErrorID();
            }
        } else {
            this.showError();
        }
    }

    showError() {
        this.messages.show({
            severity: 'error',
            summary: 'Error id-email: ',
            detail: ' It is necessary and ID or your email'
        });
    }

    showErrorID() {
        this.messages.show({
            severity: 'error',
            summary: 'Error id ',
            detail: ' Invalid ID'
        });
    }

    hideDialog() {
        this.setState({
            submitted: false,
            productDialog: false
        });
    }

    render() {
        return (
            <div style={{width:'80%', margin:'0 auto', marginTop:'40px'}}>
                <Messages ref={el => (this.messages = el)} />
                <Card title="My Rent" footer={this.footerCard}>
                    <div className="col-12 md:col-4">
                        <div className="p-inputgroup">
                            <InputText id="id_email"
                                       onChange={(e) => this.onInputChange(e)}
                                       required autoFocus
                                       className={classNames({ 'p-invalid': this.state.submitted && !this.state.id_email })}/>
                            <Button icon="pi pi-search" className="p-button-success" onClick={this.searchRent}/>
                        </div>
                    </div>
                </Card>
                <Dialog visible={this.state.productDialog} style={{ width: '450px' }} header="Product Details" modal className="p-fluid" footer={productDialogFooter} onHide={this.hideDialog}>
                    {this.state.product.image && <img src={`images/product/${this.state.product.image}`} onError={(e) => e.target.src='https://www.primefaces.org/wp-content/uploads/2020/05/placeholder.png'} alt={this.state.product.image} className="product-image block m-auto pb-3" />}
                    <div className="field">
                        <label htmlFor="name">Name</label>
                        <InputText id="name" value={this.state.product.name} onChange={(e) => this.onInputChange(e, 'name')} required autoFocus className={classNames({ 'p-invalid': this.state.submitted && !this.state.product.name })} />
                        {this.state.submitted && !this.state.product.name && <small className="p-error">Name is required.</small>}
                    </div>
                    <div className="field">
                        <label htmlFor="description">Description</label>
                        <InputTextarea id="description" value={this.state.product.description} onChange={(e) => this.onInputChange(e, 'description')} required rows={3} cols={20} />
                    </div>

                    <div className="formgrid grid">
                        <div className="field col">
                            <label htmlFor="price">Price</label>
                            <InputNumber id="price" value={this.state.product.price} onValueChange={(e) => this.onInputNumberChange(e, 'price')} mode="currency" currency="USD" locale="en-US" />
                        </div>
                        <div className="field col">
                            <label htmlFor="quantity">Quantity</label>
                            <InputNumber id="quantity" value={this.state.product.quantity} onValueChange={(e) => this.onInputNumberChange(e, 'quantity')} integeronly />
                        </div>
                    </div>
                </Dialog>
            </div>
        );
    }
}

export default Rents;
