import './App.css';
import React, {Component} from "react";
import {CustomerService} from "./service/CustomerService";
import {CarService} from "./service/CarService";
import {Panel} from "primereact/panel";
import { DataTable } from 'primereact/datatable';
import { Column } from "primereact/column";
import { Calendar } from 'primereact/calendar';
import { Card } from 'primereact/card';
import {Button} from "primereact/button";
import { Dropdown } from 'primereact/dropdown';

import 'primereact/resources/themes/bootstrap4-dark-blue/theme.css'
import 'primereact/resources/primereact.min.css'
import 'primeicons/primeicons.css'


export class App extends Component{
    emptyProduct = {
        id: null,
        name: '',
        description: '',
        category: null,
        price: 0,
        quantity: 0,
        rating: 0,
        inventoryStatus: 'INSTOCK'
    };

    constructor(props) {
        super(props);

        let today = new Date();
        let month = today.getMonth();
        let year = today.getFullYear();
        let nextMonth = month === 11 ? 0 : month + 1;
        let nextYear = nextMonth === 0 ? year + 1 : year;

        let minDate = new Date();
        let maxDate = new Date();
        maxDate.setMonth(nextMonth);
        maxDate.setFullYear(nextYear);
        let maxDateS = new Date();
        maxDateS.setMonth(nextMonth+4);
        maxDateS.setFullYear(nextYear);

        this.state = {
            dateStart: new Date(),
            dateEnd: null,
            minDate: minDate,
            maxDate: maxDate,
            maxDateS: maxDateS,
            cars:null,
            category:'',color:'',carYear:'',model:'',makec:'',
            selectedCar: {},
            carDialog: false
        };
        this.customerService = new CustomerService();
        this.carService = new CarService();

        this.searchAvaiable = this.searchAvaiable.bind(this);
        this.footerCard = (
            <Button label="Search" style={{marginRight: '.25em'}} onClick={this.searchAvaiable}/>
        );


    }

    componentDidMount() {
        this.customerService.getCustomer("colner1@dot.gov").then(data => this.setState({personas: data}));
        this.carService.getCategory().then(data => {this.setState({categories: data});
            this.state.categories.unshift('');});
        this.carService.getColors().then(data => {this.setState({colors: data});
            this.state.colors.unshift('');});
        this.carService.getCarsY().then(data => {this.setState({carYears: data});
            this.state.carYears.unshift('');});
        this.carService.getModels().then(data => {this.setState({models: data});
            this.state.models.unshift('');});
        this.carService.getMakers().then(data => {this.setState({makers: data});
            this.state.makers.unshift('');});
    }

    searchAvaiable(){
        this.carService.getByFilters(
            this.state.category,
            this.state.color,
            this.state.carYear,
            this.state.model,
            this.state.makec,
            this.state.dateStart.toISOString().slice(0, 10),
            this.state.dateEnd.toISOString().slice(0, 10)).then(data => {this.setState({cars:data})});
    }

    rentThisCar(car)  {
        this.setState({
            car: { ...car },
            carDialog: true
        });
    }

    actionBodyTemplate(rowData) {
        return (
            <React.Fragment>
                <Button label="Rent" className="p-button-rounded p-button-success mr-2" onClick={() => this.rentThisCar(rowData)} />
            </React.Fragment>
        );
    }

    render() {
        return (
            <div style={{width:'80%', margin:'0 auto', marginTop:'10%'}}>
                <Card title="Reservacion" footer={this.footerCard}>
                    <div className='p-col-12 p-md-4'>
                        <Dropdown value={this.state.category} options={this.state.categories} onChange={e => {
                            this.setState({ category: e.value });
                        }} placeholder="Category"/>
                        <Dropdown value={this.state.color} options={this.state.colors} onChange={e => {
                            this.setState({ color: e.value });
                        }} placeholder="Color"/>
                        <Dropdown value={this.state.carYear} options={this.state.carYears} onChange={e => {
                            this.setState({ carYear: e.value });
                        }} placeholder="Year"/>
                        <Dropdown value={this.state.model} options={this.state.models} onChange={e => {
                            this.setState({ model: e.value });
                        }} placeholder="Model"/>
                        <Dropdown value={this.state.makec} options={this.state.makers} onChange={e => {
                            this.setState({ makec: e.value });
                        }} placeholder="Maker"/>
                    </div>
                    <div className='p-col-12 p-md-4'>
                        <h3>From</h3>
                        <Calendar
                            onChange={e => this.setState({ dateStart: e.value })}
                            minDate={this.state.minDate}
                            maxDate={this.state.maxDate}
                            readonlyInput={true}
                            showIcon={true}
                            dateFormat='yy-mm-dd'
                            required={true}
                        />
                    </div>
                    <div className='p-col-12 p-md-4'>
                        <h3>To</h3>
                        <Calendar
                            onChange={e => this.setState({ dateEnd: e.value })}
                            minDate={this.state.dateStart}
                            maxDate={this.state.maxDateS}
                            readonlyInput={true}
                            showIcon={true}
                            dateFormat='yy-mm-dd'
                            required={true}
                        />
                    </div>
                </Card>
                <Panel header="Rent Car">
                    <DataTable value={this.state.cars} paginator={true} rows={10} selectionMode={"single"} selection={this.state.selectedCar}
                               onSelectionChange={e => this.setState({ selectedCar: e.value })}
                               paginatorTemplate="FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink CurrentPageReport RowsPerPageDropdown">
                        <Column field="vin" header="VIN"></Column>
                        <Column field="category" header="Category"></Column>
                        <Column field="model" header="Model"></Column>
                        <Column field="make" header="Make"></Column>
                        <Column field="color" header="Color"></Column>
                        <Column field="year" header="Year"></Column>
                        <Column field="price" header="Price"></Column>
                        <Column body={this.actionBodyTemplate} exportable={false} style={{ minWidth: '8rem' }}></Column>
                    </DataTable>
                </Panel>

                <Dialog visible={this.state.carDialog} style={{ width: '450px' }} header="Product Details" modal className="p-fluid" footer={productDialogFooter} onHide={this.hideDialog}>
                    {this.state.product.image && <img src={`images/product/${this.state.product.image}`} onError={(e) => e.target.src='https://www.primefaces.org/wp-content/uploads/2020/05/placeholder.png'} alt={this.state.product.image} className="product-image block m-auto pb-3" />}
                    <div className="field">
                        <label htmlFor="name">Name</label>
                        <InputText id="name" value={this.state.product.name} onChange={(e) => this.onInputChange(e, 'name')} required autoFocus className={classNames({ 'p-invalid': this.state.submitted && !this.state.product.name })} />
                        {this.state.submitted && !this.state.product.name && <small className="p-error">Name is required.</small>}
                    </div>
                    <div className="field">
                        <label htmlFor="description">Description</label>
                        <InputTextarea id="description" value={this.state.product.description} onChange={(e) => this.onInputChange(e, 'description')} required rows={3} cols={20} />
                    </div>

                    <div className="field">
                        <label className="mb-3">Category</label>
                        <div className="formgrid grid">
                            <div className="field-radiobutton col-6">
                                <RadioButton inputId="category1" name="category" value="Accessories" onChange={this.onCategoryChange} checked={this.state.product.category === 'Accessories'} />
                                <label htmlFor="category1">Accessories</label>
                            </div>
                            <div className="field-radiobutton col-6">
                                <RadioButton inputId="category2" name="category" value="Clothing" onChange={this.onCategoryChange} checked={this.state.product.category === 'Clothing'} />
                                <label htmlFor="category2">Clothing</label>
                            </div>
                            <div className="field-radiobutton col-6">
                                <RadioButton inputId="category3" name="category" value="Electronics" onChange={this.onCategoryChange} checked={this.state.product.category === 'Electronics'} />
                                <label htmlFor="category3">Electronics</label>
                            </div>
                            <div className="field-radiobutton col-6">
                                <RadioButton inputId="category4" name="category" value="Fitness" onChange={this.onCategoryChange} checked={this.state.product.category === 'Fitness'} />
                                <label htmlFor="category4">Fitness</label>
                            </div>
                        </div>
                    </div>

                    <div className="formgrid grid">
                        <div className="field col">
                            <label htmlFor="price">Price</label>
                            <InputNumber id="price" value={this.state.product.price} onValueChange={(e) => this.onInputNumberChange(e, 'price')} mode="currency" currency="USD" locale="en-US" />
                        </div>
                        <div className="field col">
                            <label htmlFor="quantity">Quantity</label>
                            <InputNumber id="quantity" value={this.state.product.quantity} onValueChange={(e) => this.onInputNumberChange(e, 'quantity')} integeronly />
                        </div>
                    </div>
                </Dialog>
            </div>
        );
    }


}
