import React, {Component} from "react";
import {classNames} from 'primereact/utils';
import {Card} from "primereact/card";
import {Button} from "primereact/button";
import {InputText} from "primereact/inputtext";
import { Messages } from 'primereact/messages';
import {RentService} from "../service/RentService";
import validator from 'validator'


export class Rents extends Component {

    emptyRent = {
        VIN: '',
        category: '',
        make: '',
        model: '',
        year: 0,
        color: '',
        price: 0,
        description: ''
    };

    constructor(props) {
        super(props);

        this.state = {
            id_email:'',
            submitted: false,
            searchValid: false,
            rent: this.emptyRent,
            rents: {}
        }

        this.rentService = new RentService();

        this.searchRent = this.searchRent.bind(this);
        this.hideDialog = this.hideDialog.bind(this);
        this.showError = this.showError.bind(this);
        this.showErrorID = this.showErrorID.bind(this);
    }

    onInputChange(e) {
        this.state['id_email'] = ((e.target && e.target.value) || '').trim();
    }

    searchRent() {
        this.state.submitted = true;
        this.state.rent = '';
        if (this.state.id_email) {
            if (validator.isEmail(this.state.id_email)){
                console.log("Valid Email");
                //this.rentService.getRentEmail(this.state.id_email).then(data => {this.setState({rents: data})});
            } else if (validator.isInt(this.state.id_email)) {
                console.log("Valid ID");
                this.rentService.getRentId(this.state.id_email).then(data => {this.setState({rent: data})});
            } else {
                this.showErrorID();
            }
        } else {
            this.showError();
        }
    }

    showError() {
        this.messages.show({
            severity: 'error',
            summary: 'Error id-email: ',
            detail: ' It is necessary and ID or your email'
        });
    }

    showErrorID() {
        this.messages.show({
            severity: 'error',
            summary: 'Error id ',
            detail: ' Invalid ID'
        });
    }

    hideDialog() {
        this.setState({
            submitted: false,
            productDialog: false
        });
    }

    render() {
        return (
            <div style={{width:'80%', margin:'0 auto', marginTop:'40px'}}>
                <Messages ref={el => (this.messages = el)} />
                <Card title="My Rent" footer={this.footerCard}>
                    <div className="col-12 md:col-4">
                        <div className="p-inputgroup">
                            <InputText id="id_email"
                                       onChange={(e) => this.onInputChange(e)}
                                       required autoFocus
                                       className={classNames({ 'p-invalid': this.state.submitted && !this.state.id_email })}/>
                            <Button icon="pi pi-search" className="p-button-success" onClick={this.searchRent}/>
                        </div>
                    </div>
                </Card>
                <div>{this.state.rent}</div>
            </div>
        );
    }
}

export default Rents;
